function filter(){$("#filter").on("change",function(){var e=$(this).val();"responsavel"==e?($("#filter-data, #filter-sala").hide(),$("#filter-responsavel").show()):"sala"==e?($("#filter-data, #filter-responsavel").hide(),$("#filter-sala").show()):"data"==e&&($("#filter-responsavel, #filter-sala").hide(),$("#filter-data").show())})}function datePicker(){$("#form-reservar #data, #filter-data").datepicker({format:"dd/mm/yyyy",weekStart:0,maxViewMode:3,todayBtn:"linked",clearBtn:!0,language:"pt-BR",orientation:"bottom right",multidate:!1,daysOfWeekDisabled:"0",daysOfWeekHighlighted:"0",autoclose:!0,todayHighlight:!0,toggleActive:!0})}$(document).ready(function(){filter(),datePicker()});